import React, {useState, useEffect} from 'react';
import api from './services/api'

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';



function App() {

  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [github_username, setGithubUsername] = useState('');
  const [techs, setTechs] = useState('');



  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const {latitude, longitude} = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);


      },
      (err) => {
        console.log(err);
      },
      {
        timeout: 30000,
      }
    );
  }, []);

  async function handleAddDev(e) {
    e.preventDefault();
  }

  return (

    <div id="app">
      <aside>
        <strong>Cadastrar</strong>
        <form onSubmit={handleAddDev}>

            <div className="input-block">
              <label htmlFor="github_username">Usuário do Github</label>
              <input
              name="github_username"
              id="github_username"
              required
              value={github_username}
              onChange={e => setGithubUsername(e.target.value)}

              />
            </div>

            <div className="input-block">
              <label htmlFor="techs">Tecnologias</label>
              <input
              name="techs"
              id="techs"
              required
              value={techs}
              onChange={e => setTechs(e.target.value)}
              />
            </div>

            <div className="input-group">

              <div className="input-block">
                <label htmlFor="latitude">Latitude</label>
                <input
                type="number"
                name="latitude"
                id="latitude"
                required
                value={latitude}
                onChange={e => setLatitude(e.target.value)}
             />
              </div>

              <div className="input-block">
                <label htmlFor="longitude">Longitude</label>
                <input
                 type="number"
                 name="longitude"
                 id="longitude"
                 required
                 value={longitude}
                 onChange={e => setLatitude(e.target.value)}

                />
              </div>
            </div>

            <button type="submit">Salvar</button>

        </form>
      </aside>
      <main>

      <ul>
        <li className="dev-item"> 
        <header>
          <img src="https://scontent.fbsb8-2.fna.fbcdn.net/v/t1.0-9/20622055_887488671390383_5782802822445994289_n.jpg?_nc_cat=109&_nc_ohc=-uBpt0uaWOcAX8K4CuG&_nc_ht=scontent.fbsb8-2.fna&oh=db1e1bd667caa9e6d3bc6aec82226571&oe=5ED408CB" alt="Gabriel Angelo"></img>
          <div className="user-info">
            <strong>Gabriel Angelo</strong>
            <span>ReactJS, React-Native NodeJS</span>

            <p>Enthusiastic Programmer</p>
            <a href="github.com/angells1">Acessar Perfil no Github</a>
          </div>
        </header> 

        </li>
        <li className="dev-item"> 
        <header>
          <img src="https://scontent.fbsb8-2.fna.fbcdn.net/v/t1.0-9/20622055_887488671390383_5782802822445994289_n.jpg?_nc_cat=109&_nc_ohc=-uBpt0uaWOcAX8K4CuG&_nc_ht=scontent.fbsb8-2.fna&oh=db1e1bd667caa9e6d3bc6aec82226571&oe=5ED408CB" alt="Gabriel Angelo"></img>
          <div className="user-info">
            <strong>Gabriel Angelo</strong>
            <span>ReactJS, React-Native NodeJS</span>

            <p>Enthusiastic Programmer</p>
            <a href="github.com/angells1">Acessar Perfil no Github</a>
          </div>
        </header> 

        </li>
        <li className="dev-item"> 
        <header>
          <img src="https://scontent.fbsb8-2.fna.fbcdn.net/v/t1.0-9/20622055_887488671390383_5782802822445994289_n.jpg?_nc_cat=109&_nc_ohc=-uBpt0uaWOcAX8K4CuG&_nc_ht=scontent.fbsb8-2.fna&oh=db1e1bd667caa9e6d3bc6aec82226571&oe=5ED408CB" alt="Gabriel Angelo"></img>
          <div className="user-info">
            <strong>Gabriel Angelo</strong>
            <span>ReactJS, React-Native NodeJS</span>

            <p>Enthusiastic Programmer</p>
            <a href="github.com/angells1">Acessar Perfil no Github</a>
          </div>
        </header> 

        </li>
        <li className="dev-item"> 
        <header>
          <img src="https://scontent.fbsb8-2.fna.fbcdn.net/v/t1.0-9/20622055_887488671390383_5782802822445994289_n.jpg?_nc_cat=109&_nc_ohc=-uBpt0uaWOcAX8K4CuG&_nc_ht=scontent.fbsb8-2.fna&oh=db1e1bd667caa9e6d3bc6aec82226571&oe=5ED408CB" alt="Gabriel Angelo"></img>
          <div className="user-info">
            <strong>Gabriel Angelo</strong>
            <span>ReactJS, React-Native NodeJS</span>

            <p>Enthusiastic Programmer</p>
            <a href="github.com/angells1">Acessar Perfil no Github</a>
          </div>
        </header> 

        </li>
      </ul>

      </main>
    </div>

  );
}

export default App;
